import React from 'react';
import Header from './components/Header';

const App = () => {
  return (
    <Router>
      <div>
        <Header />  {/* Include the header */}
        <main>
          <Switch>
            <Route path="/" exact>
              <h2>Welcome to the Community Safety App</h2>
              {/* You can display incidents or other info here */}
            </Route>
            <Route path="/report">
              <h2>Report an Incident</h2>
              {/* Add the form to report incidents */}
            </Route>
            <Route path="/login">
              <h2>Login</h2>
              {/* Add login form here */}
            </Route>
          </Switch>
        </main>
      </div>
    </Router>
  );
};

export default App;
